import React, { Component } from 'react'
import logo from './../logo.svg';
import './../App.css';
import {
    BrowserRouter as Router,
    Switch,
    useLocation
  } from "react-router-dom";
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:1,
            location:[]
        };
    }
    add=(event)=>{
        event.preventDefault();
        //console.log(this.state.location);
        const data ={
            id:new Date(),
            latitude:event.target.latitude.value,
            longitude:event.target.longitude.value
        }
        this.setState({location:[...this.state.location,data]})
    }
    getLocation=(event)=>{
    }
    delete=(id)=>{
        // console.log(id)
        this.setState({
            location: this.state.location.filter((i)=>i.id !== id)
        })
    }
    render() {
        return (
            <div>
                <h1>Latitude</h1>
                <h1>Longitude</h1>
                <form onSubmit={this.add}>
                    <label>
                    <input type="number" name="latitude" />
                    </label>
                    <label>
                    <input type="number" name="longitude" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                {this.state.location.map((item) => (
                    <div>
                    <h3>{item.latitude} {item.longitude}</h3>
                    <button onSubmit={this.delete(item.id)}>
                    Delete
                    </button>
                    </div>
                ))}
                <button onSubmit={this.getLocation}>
                    Getlocation
                </button>
            </div>
        )
    }
}
