import React, { Component } from "react";
import { connect } from "react-redux";
import ShowLocation from "./ShowLocation";
import EditingLocation from "./EditingLocation";
import _ from "lodash";
class Location extends Component {
  delete = (id) => {
    this.props.deleteLocation(id)
  };
  render() {
    return (
      <div style={{display: 'table-cell'}}>
        {this.props.blogsLocation.map((item) => (item.editing ? <EditingLocation key={item.id} item={item}/> :<ShowLocation key={item.id} item={item}/>))}
        {/* <table>
          <thead>
            <tr>
              <th>Latitude</th>
              <th>Longitude</th>
            </tr>
          </thead>
          <tbody>
            {this.props.blogsLocation.map((item) => (item.editing ? <EditingLocation key={item.id} item={item}/> :<ShowLocation key={item.id} item={item}/>))}
          </tbody>
        </table> */}
        {/* {this.props.blogsLocation.map((item) => <EditingLocation key={item.id} item={item}/>)} */}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  const blogsLocation = _.map(state.blogsLocation, (val, key) => {
    return {
      ...val,
      key: key,
    };
  });
  return {
    blogsLocation,
  };
};
export default connect(mapStateToProps)(Location);
