import React, { Component } from 'react'
import logo from './../logo.svg';
import './../App.css';
import Location from './Location';
import {
    BrowserRouter as Router,
    Switch,
    useLocation
  } from "react-router-dom";
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:1,
            location:[],
            currentLocation:[],
            longitude : '',
            latitude : ''
        };
    }
    add=(event)=>{
        event.preventDefault();
        //console.log(this.state.location);
        const data ={
            id:Date.now(),
            latitude:event.target.latitude.value,
            longitude:event.target.longitude.value
        }
        this.setState({location:[...this.state.location,data]})
        console.log('this.state.currentLocation')
    }

    delete(id){
        console.log(id)
        // this.setState({location:[...this.state.location,data]})
        this.setState({
             location: [this.state.location.filter((i)=>i.id !== id)]
        })
    }
    async getLocation(){
        // console.log('this')
        // this.setState({id:2})
        // navigator.geolocation.getCurrentPosition(
        //     function(position) {
        //    var data = {
        //         latitude:position.coords.latitude,
        //         longitude:position.coords.longitude
        //     }
        //     //this.setState({currentLocation:data})
        //     // console.log("Latitude is :", position.coords.latitude);
        //     // console.log("Longitude is :", position.coords.longitude);
            
        //   }
        //   );
        var long = 'a';
        var lat = 'b';
        var a = function(pos){
            long = pos.coords.longitude
            lat = pos.coords.latitude
        }
        await navigator.geolocation.getCurrentPosition(a);
        this.setState({
             longitude: long,
             latitude: lat
        })
    }
    render() {
        return (
            <div>
                <h1>Latitude</h1>
                <h1>Longitude</h1>
                <form onSubmit={this.add}>
                    <label>
                    <input type="number" name="latitude" />
                    </label>
                    <label>
                    <input type="number" name="longitude" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                {this.state.location.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                <button onClick={()=>this.getLocation()}>
                    Getlocation
                </button>
                <h2>{this.state.id}</h2>
            </div>
        )
    }
}
