import React, { Component } from 'react'
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { EditLocation } from '../actions'
import _ from "lodash";

class EditingLocation extends Component {
  EditLocation =(event)=>{
    event.preventDefault();
    console.log(event.target.latitude.value)
  }
    render() {
        return (
            <form>
            <TextField 
            name="latitude"
            placeholder="latitude"
            size="small"
            pattern="[+-]?\d+(?:[.,]\d+)?"
            defaultValue={this.props.item.latitude}
            onChange={this.EditLocation}
            required/>
            <TextField
              label="Longitude"
              name="longitude"
              placeholder="longitude"
              size="small"
              pattern="[+-]?\d+(?:[.,]\d+)?"
              defaultValue={this.props.item.longitude}
              onChange={this.EditLocation}
              required
            />
            </form>         
        )
    }
}
export default connect(null,{EditLocation})(EditingLocation);
