export default function (state = [], action){
    switch (action.type) {
      case 'ADD_LOCATION':
        return {
          ...state,
          blogsLocation:[...state,action.payload]
        };
      case 'DELETE_LOCARION':
        return state.filter((post) => post.id !== action.id);
      default:
        return state;
    }
  };
