import React, { Component } from 'react'
import './../App.css';
import Location from './Location';
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:1,
            location:[],
            currentLocation:[],
        };
    }
    componentDidUpdate(){
        console.log(this.state.location.length)
    }
    add=(event)=>{
        event.preventDefault();
        //console.log(this.state.location);
        // console.log(this.state.location.length)
        if (this.state.location.length<4) {
            const data ={
                id:Date.now(),
                latitude:event.target.latitude.value,
                longitude:event.target.longitude.value
            }
            this.setState({location:[...this.state.location,data]})
        }else{
            alert('Error')
        }
    }

    delete=(id)=>{
        console.log(id)
        // this.setState({location:[...this.state.location,data]})
        this.setState({location: this.state.location.filter((i)=>i.id !== id)})
    }
    getLocation=async()=>{
        let promise = new Promise(function (resolve, reject) {
            navigator.geolocation.getCurrentPosition(resolve, reject);
          });
          let pos = await promise;
          let data ={
            id:Date.now(), 
            longitude: pos.coords.longitude,
            latitude: pos.coords.latitude
          }
          this.setState({currentLocation:[data]})
    }
    checklocation=()=>{
        console.log(this.state.location.length)
        if (this.state.location.length==4&&this.state.currentLocation!=[]) {
        const latitude1=[];
        const longitude1=[];
        this.state.location
        .sort(({ latitude: previousID }, { latitude: currentID }) => previousID - currentID)
        .map((latitude)=>{
            latitude1.push(latitude)
        })
        this.state.location
        .sort(({ longitude: previousID }, { longitude: currentID }) => previousID - currentID)
        .map((longitude)=>{
            longitude1.push(longitude)
        })
        console.log('Current',this.state.currentLocation[0].latitude)
        console.log(latitude1[0].latitude,latitude1[3].latitude,longitude1[0].longitude,longitude1[3].longitude)
        if (latitude1[0].latitude>0&&latitude1[3].latitude<1000) {
            alert('complete')
        }else{
            alert('faild')
        }
    }else{
        alert('Please add location')
    }
    }
    render() {
        return (
            <div>
                <h1>Latitude</h1>
                <h1>Longitude</h1>
                <form onSubmit={this.add}>
                    <label>
                    <input type="number" name="latitude" />
                    </label>
                    <label>
                    <input type="number" name="longitude" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                {this.state.location.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                <button onClick={()=>this.getLocation()}>
                    Getlocation
                </button>
                {this.state.currentLocation.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                <button onClick={()=>this.checklocation()}>
                    Check-Location
                </button>
                {/* <h2>{this.state.currentLocation[0].latitude}{this.state.currentLocation[0].longitude}</h2> */}
            </div>
        )
    }
}
