import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {connect} from 'react-redux';
import {addLocation} from '../actions/index'

class PostFormLocation extends Component {
    add=(event)=>{
        event.preventDefault();
        if (this.state.location.length<4) {
            const data ={
                id:Date.now(),
                latitude:event.target.latitude.value,
                longitude:event.target.longitude.value
            }
            this.setState({location:[...this.state.location,data]})
        }else{
            alert('Sorry, Location can add 4 limit.')
        }
        event.preventDefault();
        const data ={
            id:Date.now(),
            latitude:event.target.latitude.value,
            longitude:event.target.longitude.value
        }
        this.props.addLocation(data)
    }
    render() {
        return (
            <div>
                <form onSubmit={this.add}>
                    <label style={{paddingLeft:20}}>
                    <TextField label="Latitude"  type="number" name="latitude" placeholder="latitude" size='small'/>
                    </label>
                    <label style={{paddingLeft:20}}>
                    <TextField label="Longitude" type="number" name="longitude" placeholder="longitude" size='small'/>
                    </label>
                    <label style={{paddingTop:20}}>
                    <Button size='small' variant="contained" color="secondary" type="submit" value="Submit" >Add</Button>
                    </label>
                </form>
            </div>
        )
    }
}

export default connect(null,(addLocation))(PostFormLocation);
