import React, { Component } from 'react'
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import { deleteLocation,EditLocation } from '../actions'
import _ from "lodash";

class ShowLocation extends Component {
    render() {
        return (
            <tr key={this.props.item.id}>
            <td>{this.props.item.latitude}</td>
            <td>{this.props.item.longitude}</td>
            <td>
              <Button
                size="small"
                variant="contained"
                color="secondary"
                onClick={() => this.props.EditLocation(this.props.item.id)}>Edit</Button>
            </td>
            <td>
              <Button
                size="small"
                variant="contained"
                color="secondary"
                onClick={() => this.props.deleteLocation(this.props.item.id)}>Delete</Button>
            </td>
          </tr>
        )
    }
}
const mapStateToProps = (state) => {
    const blogsLocation = _.map(state.blogsLocation, (val, key) => {
      return {
        ...val,
        key: key,
      };
    });
    return {
      blogsLocation,
    };
  };
  export default connect(mapStateToProps,{deleteLocation,EditLocation})(ShowLocation);
