import React, { Component } from 'react'
import logo from './../logo.svg';
import './../App.css';
import {
    BrowserRouter as Router,
    Switch,
    useLocation
  } from "react-router-dom";
export default class homscreens extends Component {
    add(event){
        event.preventDefault();
        console.log(event.target.latitude.value)
        console.log(event.target.longitude.value)
    }
    getLocation(event){
        event.preventDefault();
        navigator.geolocation.getCurrentPosition(function(position) {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
          });
    }
    render() {
        return (
            <div>
                <h1>Latitude</h1>
                <h1>Longitude</h1>
                <form onSubmit={this.add}>
                    <label>
                    <input type="number" name="latitude" required/>
                    </label>
                    <label>
                    <input type="number" name="longitude" required/>
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                <button onSubmit={this.getLocation}>
                    Getlocation
                </button>
            </div>
        )
    }
}
