export default function (state = [], action){
    switch (action.type) {
      case 'ADD_LOCATION':
        return state.map((post) => {
              return {
                  ...post,
                  title: action.data.newTitle,
                  message: action.data.newMessage,
                  editing: !post.editing           
          }
      })
      default:
        return state;
    }
  };
