import React, { Component } from 'react'
import './../App.css';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location:[],
            currentLocation:[],
        };
    }
    componentDidUpdate(){
        console.log(this.state.location.length)
    }
    add=(event)=>{
        event.preventDefault();
        if (this.state.location.length<4) {
            const data ={
                id:Date.now(),
                latitude:event.target.latitude.value,
                longitude:event.target.longitude.value
            }
            this.setState({location:[...this.state.location,data]})
        }else{
            alert('Sorry, Location can add 4 limit.')
        }
    }

    delete=(id)=>{
        this.setState({location: this.state.location.filter((i)=>i.id !== id)})
    }
    getLocation=async()=>{
        let promise = new Promise(function (resolve, reject) {
            navigator.geolocation.getCurrentPosition(resolve, reject);
          });
          let pos = await promise;
          let data ={
            id:Date.now(), 
            longitude: pos.coords.longitude,
            latitude: pos.coords.latitude
          }
          this.setState({currentLocation:[data]})
    }
    checklocation=()=>{
         if (this.state.location.length==4&&this.state.currentLocation.length!==0){
         const SortLatitude=[];
         const SortLongitude=[];
         this.state.location
         .sort(({ latitude: previousLaitude }, { latitude: currentLaitude }) => previousLaitude - currentLaitude)
         .map((item)=>{
            SortLatitude.push(item.latitude)
         })
         this.state.location
         .sort(({ longitude: previousLongitude }, { longitude: currentLongitude }) => previousLongitude - currentLongitude)
         .map((item)=>{
            SortLongitude.push(item.longitude)
         })
         console.log('My Location latituide is '+this.state.currentLocation[0]+'longitude is '+this.state.currentLocation[0].longitude)
         console.log('Shorest latitude is '+SortLatitude[0]+' far latitude is '+ SortLatitude[3])
         console.log('Shorest long is '+SortLongitude[0]+' far long is '+ SortLongitude[3])
        if (this.state.currentLocation[0].latitude>SortLatitude[0]&&
            this.state.currentLocation[0].latitude<SortLatitude[3]&&
            this.state.currentLocation[0].longitude>SortLongitude[0]&&
            this.state.currentLocation[0].longitude<SortLongitude[3]) {
              alert('Your position is close to the scope of the company.')
          }else{
              alert('Your position is not close to the scope of the company.')
        }
     }else{
         alert('Please add location or getLocation')
     }
    }
    render() {
        return (
            <div className="App">
                <div style={{paddingTop:20}}>
                    <h1>Check-Location Web App</h1>
                </div>
                <div style={{paddingTop:25}}>
                {/* <form onSubmit={this.add}>
                    <label style={{paddingLeft:20}}>
                    <TextField label="Latitude"  type="number" name="latitude" placeholder="latitude" size='small'/>
                    </label>
                    <label style={{paddingLeft:20}}>
                    <TextField label="Longitude" type="number" name="longitude" placeholder="longitude" size='small'/>
                    </label>
                    <label style={{paddingTop:20}}>
                    <Button size='small' variant="contained" color="secondary" type="submit" value="Submit" >Add</Button>
                    </label>
                </form> */}
                </div>
                <div className="centered" style={{paddingTop:25}}>
                <table>
                <thead>
                    <tr>
                        <th>Latitude</th>
                        <th>Longitude</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.location.map((item) => (
                   <tr key={item.id}>
                       <td>{item.latitude}</td>
                       <td>{item.longitude}</td>
                       <td><Button size='small' variant="contained" color="secondary" onClick={()=>this.delete(item.id)}>Delete</Button></td>
                   </tr>
                ))}
                </tbody>
                </table>
                </div>
                {/* {this.state.location.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))} */}
                <div className="centered" style={{paddingTop:50}}>
                <Button size='small' variant="contained" color="primary" onClick={()=>this.getLocation()}>Getlocation</Button>
                </div>
                <div style={{paddingTop:25,paddingLeft:50}}>
                {this.state.currentLocation.map((item) => (
                    <div>
                        <h2 className="font-getLocation">Current Location<br />Latitude: {item.latitude} <br />Longitude: {item.longitude}</h2>
                    </div>
                    // <h1>Longitude: {item.longitude}</h1>
                    // <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                </div>
                <div className="centered" style={{paddingTop:50}}>
                <Button size='small' variant="contained" color="secondary" onClick={()=>this.checklocation()}>Check-Location</Button>
                </div>
                {/* <h2>{this.state.currentLocation[0].latitude}{this.state.currentLocation[0].longitude}</h2> */}
            </div>
        )
    }
}
