import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import { deleteLocation } from '../actions'
import Table from "./Table";
import Editing from "./Editing";
import _ from "lodash";
class ShowLocation extends Component {
  delete = (id) => {
    this.props.deleteLocation(id)
  };
  render() {
    return (
      <div>
        <table>
          <thead>
            <tr>
              <th>Latitude</th>
              <th>Longitude</th>
            </tr>
          </thead>
          <tbody>
          {this.props.blogsLocation.map((item) => (
              item.editing ? <Table key={item.id} item={item}/> :<Table key={item.id} item={item}/>
              ))}
          </tbody>
        </table>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  const blogsLocation = _.map(state.blogsLocation, (val, key) => {
    return {
      ...val,
      key: key,
    };
  });
  return {
    blogsLocation,
  };
};
export default connect(mapStateToProps,{deleteLocation})(ShowLocation);
