export default function (state = [], action){
    switch (action.type) {
      case 'ADD_LOCATION':
        return state.concat([action.payload])
      case 'DELETE_LOCATION':
        return state.filter((post) => post.id !== action.payload);
      default:
        return state;
    }
  };
