import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
export default class GetMyLocation extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location:[],
            currentLocation:[],
        };
    }
    getLocation=async()=>{
        let promise = new Promise(function (resolve, reject) {
            navigator.geolocation.getCurrentPosition(resolve, reject);
          });
          let pos = await promise;
          let data ={
            id:Date.now(), 
            longitude: pos.coords.longitude,
            latitude: pos.coords.latitude
          }
          this.setState({currentLocation:[data]})
    }
    render() {
        return (
            <div>
                <Button size='small' variant="contained" color="primary" onClick={()=>this.getLocation()}>Getlocation</Button>
                {this.state.currentLocation.map((item) => (
                    <div>
                        <h2 className="font-getLocation">Current Location<br />Latitude: {item.latitude} <br />Longitude: {item.longitude}</h2>
                    </div>
                ))}
            </div>
        )
    }
}
