import React, { Component } from 'react'
import logo from './../logo.svg';
import './../App.css';
import {
    BrowserRouter as Router,
    Switch,
    useLocation
  } from "react-router-dom";
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location:[{latitude:'1',longitude:'2'},{latitude:'1',longitude:'2'}]
        };
    }
    add=(event)=>{
        event.preventDefault();
        //console.log(this.state.location);
        const data ={
            latitude:1,
            longitude:2
        }
        this.setState({location:[...this.state.location,data]})
    }
    getLocation=(event)=>{
        navigator.geolocation.getCurrentPosition(function(position) {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
          });
    }
    render() {
        return (
            <div>
                <h1>Latitude</h1>
                <h1>Longitude</h1>
                <form onSubmit={this.add}>
                    <label>
                    <input type="number" name="latitude" />
                    </label>
                    <label>
                    <input type="number" name="longitude" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                {this.state.location.map(item => (
                    <h3>{item.latitude} {item.longitude}</h3>
                ))}
                <button onSubmit={this.getLocation}>
                    Getlocation
                </button>
            </div>
        )
    }
}
