import React, { Component } from 'react'
import './../App.css';
import Location from './Location';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
export default class homscreens extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id:1,
            location:[],
            currentLocation:[],
        };
    }
    componentDidUpdate(){
        console.log(this.state.location.length)
    }
    add=(event)=>{
        event.preventDefault();
        //console.log(this.state.location);
        // console.log(this.state.location.length)
        if (this.state.location.length<4) {
            const data ={
                id:Date.now(),
                latitude:event.target.latitude.value,
                longitude:event.target.longitude.value
            }
            this.setState({location:[...this.state.location,data]})
        }else{
            alert('Error')
        }
    }

    delete=(id)=>{
        console.log(id)
        // this.setState({location:[...this.state.location,data]})
        this.setState({location: this.state.location.filter((i)=>i.id !== id)})
    }
    getLocation=async()=>{
        let promise = new Promise(function (resolve, reject) {
            navigator.geolocation.getCurrentPosition(resolve, reject);
          });
          let pos = await promise;
          let data ={
            id:Date.now(), 
            longitude: pos.coords.longitude,
            latitude: pos.coords.latitude
          }
          this.setState({currentLocation:[data]})
    }
    checklocation=()=>{
         if (this.state.location.length==4&&this.state.currentLocation.length==0){
         const latitude1=[];
         const longitude1=[];
         this.state.location
         .sort(({ latitude: previousID }, { latitude: currentID }) => previousID - currentID)
         .map((latitude)=>{
             latitude1.push(latitude)
         })
         this.state.location
         .sort(({ longitude: previousID }, { longitude: currentID }) => previousID - currentID)
         .map((longitude)=>{
             longitude1.push(longitude)
         })
    //     console.log('Current',this.state.currentLocation[0].latitude)
    //     console.log(latitude1[0].latitude,latitude1[3].latitude,longitude1[0].longitude,longitude1[3].longitude)
         if (latitude1[0].latitude>0&&latitude1[3].latitude<1000) {
             alert('complete')
         }else{
             alert('faild')
         }
     }else{
         alert('Please add location or getLocation')
     }
    }
    render() {
        return (
            <div className="App">
                <header className="App-header">
                <div>
                    <h3>Latitude Longitude</h3>
                    </div>
                <form onSubmit={this.add}>
                    <label>
                    Latitude Longitude  <input type="number" name="latitude" />
                    </label>
                    <label>
                    <input type="number" name="longitude" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
                
                {this.state.location.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                <button onClick={()=>this.getLocation()}>
                    Getlocation
                </button>
                {this.state.currentLocation.map((item) => (
                    <Location latitude={item.latitude} longitude={item.longitude} key={item.id} click={()=>this.delete(item.id)}/>
                ))}
                <button onClick={()=>this.checklocation()}>
                    Check-Location
                </button>
                <TableContainer component={Paper}>>
                    <Table>
                        <TableHead>
                            <TableRow>
                            <TableCell>Dessert (100g serving)</TableCell>
                            <TableCell align="right">Calories</TableCell>
                            <TableCell align="right">Fat&nbsp;(g)</TableCell>
                            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
                            <TableCell align="right">Protein&nbsp;(g)</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                        <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
            <TableCell align="right">Protein&nbsp;(g)</TableCell>
                        </TableBody>
                    </Table>
                </TableContainer>
                {/* <h2>{this.state.currentLocation[0].latitude}{this.state.currentLocation[0].longitude}</h2> */}
                </header>

            </div>
        )
    }
}
