import React, { Component } from 'react'
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { EditLocation } from '../actions'
import _ from "lodash";

class EditingLocation extends Component {
  EditLocation =(event)=>{
    event.preventDefault();
    console.log(event.target.latitude.value)
  }
    render() {
        return (
          <div style={{float:'left'}}>
            <form>
            <label style={{ paddingLeft: 20 }}>
            <TextField 
            name="latitude"
            placeholder="latitude"
            size="small"
            pattern="[+-]?\d+(?:[.,]\d+)?"
            defaultValue={this.props.item.latitude}
            onChange={this.EditLocation}
            required/>
            </label> 
            <label >
            <TextField
              label="Longitude"
              name="longitude"
              placeholder="longitude"
              size="small"
              pattern="[+-]?\d+(?:[.,]\d+)?"
              defaultValue={this.props.item.longitude}
              onChange={this.EditLocation}
              required
            />
            </label>

            </form>
            </div>
          
        )
    }
}
export default connect(null,{EditLocation})(EditingLocation);
