import Button from '@material-ui/core/Button';
import {connect} from 'react-redux';
import _ from 'lodash';
class GetMyLocation extends Component {
    getLocation=async()=>{
        let promise = new Promise(function (resolve, reject) {
            navigator.geolocation.getCurrentPosition(resolve, reject);
          });
          let pos = await promise;
          let data ={
            id:Date.now(), 
            longitude: pos.coords.longitude,
            latitude: pos.coords.latitude
          }
        //   this.setState({currentLocation:[data]})
    }
    render() {
        return (
            <div>
                <Button size='small' variant="contained" color="primary" onClick={()=>this.getLocation()}>Getlocation</Button>
                {/* {this.state.currentLocation.map((item) => (
